import React, {Component} from "react";
import "./style.css";
import Product from "./Product";
import {
    useQuery,
    gql
} from "@apollo/client";

const categoryQuery = gql`query GetCategoryProducts{
    category(input:{
        title: "clothes",
    }){
        products{
            prices{
                currency
                amount
            },
            gallery,
            id,
            inStock
        }
    }
}`

const GetCategoryProducts = ()=>{
    const {loading, error, data} = useQuery(categoryQuery)

    if(loading) return <p>Loading...</p>
    if(error) return <p>Error...</p>

    console.log(data)
    console.log( data.category.products.forEach((product)=>{
        <Product image={product.gallery[0]} name={product.id} price={product.prices[0].currency + product.prices[0].amount }/>
    }))

    return <p>hello</p>
}

const Catalog = (props)=>{
    return(<>
        <button className="button" id="category-button" onClick={()=>{props.changeCategory()}}>{props.currentCategory}</button>

        <section id="catalog-container">
            <GetCategoryProducts/>
            <Product id="test" image="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" name="Apollo Running Short" price="$50.00"/>
            <Product id="test" image="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" name="test" price="test"/>
            <Product id="test" image="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" name="test" price="test"/>
            <Product id="test" image="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" name="test" price="test"/>
            <Product id="test" image="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" name="test" price="test"/>
        </section>
    </>);
}

