import React, {Component,useEffect} from "react";
import "./style.css";
import Product from "./Product";
import {
    useQuery,
    gql
} from "@apollo/client";

const clothesCategoryQuery = gql`query GetCategoryProducts{
    category(input:{
        title: "clothes",
    }){
        products{
            prices{
                currency
                amount
            },
            gallery,
            id,
            inStock
        }
    }
}`

const techCategoryQuery = gql`query GetCategoryProducts{
    category(input:{
        title: "tech",
    }){
        products{
            prices{
                currency
                amount
            },
            gallery,
            id,
            inStock
        }
    }
}`

const updateURL = ()=>{window.location.search="catalog"}

const GetCategoryProducts = (props)=>{
    const {loading, error, data} = useQuery(props.category === "Clothes" ? clothesCategoryQuery : techCategoryQuery)


    if(loading) return <p>Loading...</p>
    if(error) return <p>Error...</p>

    var Products = []
    data.category.products.forEach((product)=>{
        Products.push(<Product image={product.gallery[0]} name={product.id} prices={product.prices} key={product.id} currentCurrency={props.currentCurrency}/>)
    })

    document.querySelector("#catalog-container").style.opacity = "1"

    return Products
}

const Catalog = (props)=>{

    useEffect(()=>{
        console.log(props)
    })

    return(<>
        <button className="button" id="category-button" onClick={()=>{props.changeCategory()}}>{props.currentCategory}</button>

        <section id="catalog-container">
            <GetCategoryProducts category={props.currentCategory} currentCurrency={props.currentCurrency}/>
            {/*<Product id="test" image="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" name="Apollo Running Short" price="$50.00"/>*/}
