import React, {Component} from "react";
import "./style.css";

function beautifyName(name){
    var newName = name[0].toUpperCase()
    for(let i = 1 ; i < name.length ; i++){
        if(name[i] === '-' && i !== name.length-1){
            newName += " "
            newName += name[i+1].toUpperCase()
            i+=1
        }
        else
            newName += name[i]
    }
    return newName
}

class Product extends Component{
    componentDidMount() {
        this.transformPrice(this.props.currentCurrency,[1])
    }

    transformPrice(currentCurrency,pricesArray){
        console.log("Currency: ",currentCurrency)
    }

    render() {
        return(<>
                <div className="Product">
                    <img src={this.props.image}/>
                    <p className="product-name">{beautifyName(this.props.name)}</p>
                    {/*<p className="product-price">{this.props.prices}</p>*/}
                </div>
            </>)
    }
}

export default Product;
