import React, {Component,useState} from "react";

import './style.css'
import {
    useQuery,
    gql
} from "@apollo/client";

const testQuery = gql`query getProduct{
    category(input:{
        title: "clothes",
    }){
        products{
            prices{
                currency
                amount
            },
            category,
            description,
            attributes{
                id,
                items{
                    displayValue,
                    value,
                    id
                }
            }
            gallery,
            id,
            inStock
        }
    }  }`

const GetProductGallery = (props)=>{
    const {loading, error, data} = useQuery(testQuery)

    if(loading) return <p>Loading...</p>
    if(error) return <p>Error...</p>

    data.category.products.forEach((product)=>{
        if(product.id === "jacket-canada-goosee"){
            props.setProductName(product.id)
        }
    })
    return <p>hello</p>
}


const ProductPage = (props)=>{
    window.location.hash = "product"

    const [mainGallery,setMainGallery] = useState([])
    const [sideGallery,setSideGallery] = useState([])
    const [productName,setProductName] = useState("")


    return(<>
        <section id="page-product">
            <div className="product-gallery-container">
                <div className="side-gallery">
                    <GetProductGallery
                        setProductName={setProductName}
                    />
                    <img src="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087"/>
                    <img src="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087"/>
                    <img src="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087"/>
                </div>

                <div className="main-gallery">
                    <img src="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087"/>
                </div>
            </div>

            <div className="product-options-container">

                <div className="product-name">
                    <h2>{productName}</h2>
                    <h3>Running short</h3>
                </div>

                <div className="size-container">
                    SIZE:
                    <div className="size-boxes">
                        <button className="button">XS</button>
                        <button className="button">S</button>
                        <button className="button">M</button>
                        <button className="button">L</button>
                    </div>
                </div>

                <div className="price-container">
                    PRICE:
                    <p>$50.00</p>

                </div>

                <div className="addToCartBtn">
                    <button className="button">ADD TO CART</button>
                </div>

                <div className="description-container">

                    <p>Find stunning women's cocktail dresses and party dresses. Stand out in lace and metallic cocktail dresses and party dresses from all your favorite brands.</p>
                </div>

            </div>

        </section>

        </>)
}

export default ProductPage
