import React, {Component,useState,useEffect} from "react";

import './style.css'
import {
    useQuery,
    gql
} from "@apollo/client";

function beautifyName(name){
    //removes dashes from name, adds uppercase to first letter of each word
    var newName = name[0].toUpperCase()
    for(let i = 1 ; i < name.length ; i++){
        if(name[i] === '-' && i !== name.length-1){
            newName += " "
            newName += name[i+1].toUpperCase()
            i+=1
        }
        else
            newName += name[i]
    }
    return newName
}

function transformPrice(pricesArray,currentCurrency) {
    //renders the right prace for an object, based on its array of prices
    var Price = ""
    var found = false;
    pricesArray.forEach((price) => {
        if (price.currency === currentCurrency) {
            Price += price.currency + price.amount
            found = true;
        }

    })
    if (!found)
        return "currency-not-found-in-database"
    else {
        Price = Price.replace("EUR", "€")
        Price = Price.replace("USD", "$")
        Price = Price.replace("JPY", "¥")
        return Price
    }
}

const testQuery = gql`query getProduct{
    category(input:{
        title: "clothes",
    }){
        products{
            prices{
                currency
                amount
            },
            category,
            description,
            attributes{
                id,
                items{
                    displayValue,
                    value,
                    id
                }
            }
            gallery,
            id,
            inStock
        }
    }  }`

const GetData = ()=>{
    const {loading, error, data} = useQuery(testQuery)
    const URL = window.location.search.substring(1)
    console.log(URL)

    var PRODUCT = null
    // var sideGallery = []
    data.category.products.forEach((product)=>{
        if(product.id === URL){
            PRODUCT = product
            // props.setProductName(product.id)
            // props.setPrice(transformPrice(product.prices,props.currentCurrency))
            // props.setMainGallery(product.gallery[0])
            // props.setGalleryLinks(product.gallery)
            // document.getElementsByClassName("description-container")[0].innerHTML = product.description
            // product.gallery.forEach((photo)=>{
            //     sideGallery.push(<img src={photo}/>)
            }
})
    return PRODUCT
}


const ProductPage = (props)=>{

    const [sideGallery,setSideGallery] = useState([])
    const [mainGallery,setMainGallery] = useState("n/a")
    const [galleryLinks,setGalleryLinks] = useState([])
    const [productName,setProductName] = useState("n/a")
    const [brand,setBrand] = useState("no-brand-in-queries")
    const [price,setPrice] = useState("n/a")
    const [description,setDescription] = useState("n/a")

    const [selectedSize,selectSize] = useState("1")

    useEffect(() => {
        window.location.hash = "product"
        var product = GetData()
        // console.log(product)

    }, []);



    return(<>
        <section id="page-product">
            <div className="product-gallery-container">
                <div className="side-gallery">
                    {/*<GetProductGallery*/}
                    {/*    setProductName={setProductName}*/}
                    {/*    setMainGallery={setMainGallery}*/}
                    {/*    mainGallery={mainGallery}*/}
                    {/*    galleryLinks={galleryLinks}*/}
                    {/*    setGalleryLinks={setGalleryLinks}*/}
                    {/*    setPrice={setPrice}*/}
                    {/*    currentCurrency={props.currentCurrency}*/}
                    {/*    setDescription={setDescription}*/}
                    {/*/>*/}
                </div>

                <div className="main-gallery">
                    <img src={mainGallery}/>
                </div>
            </div>

            <div className="product-options-container">

                <div className="product-name">
                    <h2>{beautifyName(productName)}</h2>
                    <h3>{brand}</h3>
                </div>

                <div className="size-container">
                    SIZE:
                    <div className="size-boxes">
                        <button className={"button" + (selectedSize===0 ? " selected" : "")} disabled onClick={()=>{selectSize(0)}}>XS</button>
                        <button className={"button" + (selectedSize===1 ? " selected" : "")} onClick={()=>{selectSize(1)}}>S</button>
                        <button className={"button" + (selectedSize===2 ? " selected" : "")} onClick={()=>{selectSize(2)}}>M</button>
                        <button className={"button" + (selectedSize===3 ? " selected" : "")} onClick={()=>{selectSize(3)}}>L</button>
                    </div>
                </div>

                <div className="price-container">
                    PRICE:
                    <p>{price}</p>

                </div>

                <div className="addToCartBtn">
                    <button className="button">ADD TO CART</button>
                </div>

                <div className="description-container">
                </div>

            </div>

        </section>

        </>)
}

export default ProductPage
